import "./Card1.css"
import React, { useState, useEffect } from 'react';


function Card1() {

    useEffect(() => {

        const textArray = ["Academics", "Projects", "Courses", "Skills", "Interests", "Portfolio", "Career"];
        const subjectsArray = ['Algebra', 'Calculus', 'Trigonometry']
        const typingDelay = 80;
        const erasingDelay = 70;
        const newTextDelay = 2250; // Delay between current and next text
        let textArrayIndex = 0;
        let charIndex = 0;

        function type(cursorSpan, typedTextSpan, textArray, typingDelay, newTextDelay) {
            if (charIndex < textArray[textArrayIndex].length) {
            if(!cursorSpan.classList.contains("typing")) cursorSpan.classList.add("typing");
            typedTextSpan.textContent += textArray[textArrayIndex].charAt(charIndex);
            charIndex++;
            setTimeout(type, typingDelay);
            } 
            else {
            cursorSpan.classList.remove("typing");
                setTimeout(erase, newTextDelay);
            }
        }

        function erase(cursorSpan, typedTextSpan, textArray, erasingDelay) {
            if (charIndex > 0) {
            if(!cursorSpan.classList.contains("typing")) cursorSpan.classList.add("typing");
            typedTextSpan.textContent = textArray[textArrayIndex].substring(0, charIndex-1);
            charIndex--;
            setTimeout(erase, erasingDelay);
            } 
            else {
            cursorSpan.classList.remove("typing");
            textArrayIndex++;
            if(textArrayIndex>=textArray.length) textArrayIndex=0;
            setTimeout(type, typingDelay + 500);
            }
        }
    }, []);


    return(
        <div className="view1">
            <div className="view1_box">
                <div className="titlebox">
                    <p>Elevate your <span className="typed-text">{typedTextSpan}</span><span className="cursor">{cursorSpan}&nbsp;</span></p>
                    <h4>
                    A personalized approach to online mentorship. Guiding students at every step of their academic career.
                    <br />
                    <br />
                    Mathematics, Science, English? No Bullshit.
                    </h4>
                    <div className="view1_buttonbox">
                    <button id="learn-more-button">I am a Parent/Guardian<i className="fa-solid fa-arrow-pointer"></i></button>
                    <button id="learn-more-button">I am a Student<i className="fa-solid fa-arrow-pointer"></i></button>

                    </div>
                </div>
                <img src="https://uploads-ssl.webflow.com/605190260cb9b4c3a9de71c8/62e321dfb98f61b7bd5f2173_banner-smart-object.png"/>
            </div>
            </div>);
}


export default Card1;
